import 'ol/ol.css';
import Map from 'ol/Map';
import View from 'ol/View';
import { Draw, Modify, Snap } from 'ol/interaction';
import { Tile as TileLayer, Vector as VectorLayer } from 'ol/layer';
import { OSM, Vector as VectorSource } from 'ol/source';
import { Fill, Icon, Stroke, Style } from 'ol/style';
import Select from 'ol/interaction/Select';
import Styles from './js/styles.js';
import Loader from './js/loader.js';





var raster = new TileLayer({
  source: new OSM()
});

var color = '#FF11A3';
var styles = new Styles();
var styleFunction = function (feature) {
  styles.style(feature, color);
};

var source = new VectorSource();

var arrow = new VectorLayer({
  source: source,
  style: styleFunction
});

var vector = new VectorLayer({
  source: source,
  style: new Style({
    fill: new Fill({
      color: 'rgba(255, 255, 255, 0.2)'
    }),
    stroke: new Stroke({
      color: color,
      width: 2
    }),
    image: new Icon({
      src: './assets/marker.png',
      size: [128, 128],
      offset: [0, 0],
      opacity: 1,
      scale: 0.25
    })
  })
});

let loader = new Loader('./berlin_bezirke.geojson');
let berlin = loader.getVectorLayer();

var map = new Map({
  layers: [raster, vector, arrow, berlin],
  target: 'map',
  view: new View({
    center: [-11000000, 4600000],
    zoom: 4
  })
});


var modify = new Modify({ source: source });
map.addInteraction(modify);

var draw, snap, select; // global so we can remove them later

function addInteractions() {
  draw = new Draw({
    source: source,
    type: document.querySelector('input[name="tool"]:checked').value
  });
  map.addInteraction(draw);
  snap = new Snap({ source: source });
  map.addInteraction(snap);
  select = new Select();
  map.addInteraction(select);

}

/**
 * Handle change event.
 */
let buttons = document.getElementsByName('tool');

for (let button of buttons) {
  button.addEventListener('click', function (e) {
    map.removeInteraction(draw);
    map.removeInteraction(snap);
    map.removeInteraction(select);
    addInteractions();
  })
}

addInteractions();

let labels = document.querySelectorAll('label');

for (let label of labels) {
  label.addEventListener('click', function () {
    unstyle();
    this.style.setProperty('opacity', 1);
  });
}

function unstyle() {

  let labels = document.querySelectorAll('label');

  for (let label of labels) {
    label.style.setProperty('opacity', .3);
  }
}

document.querySelector('#color').oninput = function () {
  color = this.value;
};
